import json
def json_convert(ls, filepath):
	f_list = []
	for x in ls:
		f_list.append(json.dumps(x))
	f_list = ';'.join(f_list)
	# print f_list
	f = open(filepath, 'w')
	f.write(f_list)
	f.close()

